export const updateUser = (id, formData) => async (dispatch) => {
    dispatch({ type: "UPDATING_START" })
    try {
        const { data } = await UserApi.updateUser(id, formData);
        dispatch({ typr: "UPDTAING_SUCCESS, data: data" })
    } catch (error) {
        dispatch({ type: "UPDATING_FAIL" })
    }
}
