import React, { useState } from 'react'
import './Auth.css'
import Logo from '../../img/logo.png'
import { useDispatch, useSelector } from 'react-redux'
import { logIn } from '../../actions/AuthAction.js'
import { signUp } from '../../actions/AuthAction.js'

const Auth = () => {
    const dispatch = useDispatch()
    const loading = useSelector((state) => state.authReducer.loading)
    const [isSignUp, setIsSignUp] = useState(true)

    const [data, setData] = useState({
        firstname: "",
        lastname: "",
        password: "",
        confirmpass: "",
        username: ""
    })

    const [confirmPass, setConfirmPass] = useState(false)
    const handleChange = (e) => {
        setData({ ...data, [e.target.name]: e.target.value })
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        if (isSignUp) {
            data.password === data.confirmpass ? dispatch(signUp(data))
                : setConfirmPass(false)
        } else {
            dispatch(logIn(data))
        }
    }

    const resetForm = () => {
        setConfirmPass(true)
        setData({
            firstname: "",
            lastname: "",
            password: "",
            confirmpass: "",
            username: ""
        })

    }
    return (
        <div className="Auth">
            {/* left- side */}
            <div className='a-left'>
                <img src={Logo} alt='' />
                <div className='Webname'>
                    <h1>YYC</h1>
                    <h6>Explore the ideas of Youth</h6>
                </div>
            </div>
            {/*Right Side */}
            <div className='a-right'>
                <form className='infoForm authForm' onSubmit={handleSubmit}>
                    <h3>{isSignUp ? "Sign up" : "Log In"} </h3>

                    {isSignUp && (
                        <div>
                            <input
                                type="text"
                                placeholder="First Name"
                                className='infoInput'
                                name='firstname'
                                onChange={handleChange}
                                value={data.firstname}
                            />
                            <input
                                type="text"
                                placeholder="Last Name"
                                className='infoInput'
                                name='lastname'
                                onChange={handleChange}
                                value={data.lastname}
                            />
                        </div>
                    )}
                    <div>
                        <input
                            type="text"
                            placeholder="Usernames"
                            className='infoInput'
                            name='username'
                            onChange={handleChange}
                            value={data.username}
                        />
                    </div>
                    <div>
                        <input type="password"
                            placeholder="Password"
                            className='infoInput'
                            name='password'
                            onChange={handleChange}
                            value={data.password}
                        />

                        {isSignUp &&
                            <input type="password"
                                placeholder="Confirm Password"
                                className='infoInput'
                                name='confirmpass'
                                onChange={handleChange}
                                value={data.confirmpass}
                            />}
                    </div>
                    <span style={{ display: confirmPass ? "none" : "block", color: 'red', fontSize: '12px', alignSelf: 'flex-end', marginLeft: '5px' }}>
                        * Confirm Password is not same.
                    </span>
                    <div>
                        <span style={{ fontSize: '12px', cursor: 'pointer' }}
                            onClick={() => {
                                setIsSignUp((prev) => !prev);
                                resetForm()
                            }}
                        >{isSignUp ?
                            "Already have an account. Login!"
                            : "Don't have an account? Sign Up"}
                        </span>
                    </div>
                    <button className="button infoButton" type="submit" disabled={loading}>
                        {isSignUp ? "SignUp" : "Log In"}
                    </button>
                </form>
            </div>
        </div>
    )
}

export default Auth
