const postReducer = (
    state = {
        posts: null,
        loading: false,
        error: false,
        uploading: false
    },
    action) => {
    switch (action.type) {
        case "AUTH_START":
            return { ...state, uploading: true, error: false }
        case "AUTH_SUCCESS":
            return {
                ...state, posts: [action.data, ...state.posts],
                uploading: false, error: false
            }
        case "AUTH_FAIL":
            return { ...state, uploading: false, error: true }
        default:
            return state;
    }
}
export default postReducer;
